import React from "react";
import { useState } from "react";


const Form = () => {
  //Aqui deberan implementar el form completo con sus validaciones
  const [nombre, setNombre] = useState("");
  const [email, setEmail] = useState("");
  const [mensajeError,setMensajeError]= useState("");
  const [mensajeOk, setMensajeOk] = useState("");

  function handlerNombre(e){
    setNombre(e.target.value);
  }
  function handlerEmail(e){
    setEmail(e.target.value);
  }
  function registrar(e){
    e.preventDefault();
    if(nombre.length <6){
      setMensajeError("**Por favor verifique su información nuevamente**");
    }
    else{
      const successMsje = `Gracias ${nombre}, te contactaremos pronto vía email.`;
      setMensajeError(successMsje);
    }
  }
  return (
    <div>
      <form onSubmit={registrar}>
        <div>
          <input type="text" onChange={handlerNombre} placeholder="Ingresa tu nombre completo"/>
          <input type="email" onChange={handlerEmail} placeholder="" />
        </div>
      </form>
    </div>
  );
};

export default Form;
